def transform_grid(grid):
    rows = len(grid)
    cols = len(grid[0])
    new_grid = [row[:] for row in grid]  # Create a copy to avoid modifying the original

    for r in range(rows):
        for c in range(cols):
            if grid[r][c] == 0:
                # Check neighbors
                neighbors = []
                if r > 0:
                    neighbors.append(grid[r-1][c])
                if r < rows - 1:
                    neighbors.append(grid[r+1][c])
                if c > 0:
                    neighbors.append(grid[r][c-1])
                if c < cols - 1:
                    neighbors.append(grid[r][c+1])

                if 4 in neighbors:
                    new_grid[r][c] = 3
                elif 8 in neighbors:
                    new_grid[r][c] = 2
    return new_grid
