def transform_grid(input_grid):
    """Transforms the input grid according to the observed pattern."""

    left_col = []
    middle_col = []
    right_col = []

    rows = len(input_grid)
    cols = len(input_grid[0])

    # Extract from input grid
    for r in range(rows):
        for c in range(cols):
            if c < cols // 3 and input_grid[r][c] != 0 and not left_col:
                left_col.append(input_grid[r][c])
            elif cols // 3 <= c < 2 * cols // 3 and input_grid[r][c] != 0 and not middle_col:
                middle_col.append(input_grid[r][c])
            elif 2 * cols // 3 <= c < cols and input_grid[r][c] != 0 and not right_col:
                right_col.append(input_grid[r][c])

    left_col2 = []    
    middle_col2 = []
    right_col2 = []

    for r in range(rows-1, -1, -1):
        for c in range(cols):
            if c < cols // 3 and input_grid[r][c] != 0 and len(left_col) < 2 and not left_col2:
                left_col2.append(input_grid[r][c])
            elif cols // 3 <= c < 2 * cols // 3 and input_grid[r][c] != 0 and len(middle_col) < 2 and not middle_col2:
                middle_col2.append(input_grid[r][c])
            elif 2 * cols // 3 <= c < cols and input_grid[r][c] != 0 and len(right_col) < 2 and not right_col2:
                right_col2.append(input_grid[r][c])

    left_col3 = []    
    middle_col3 = []
    right_col3 = []

    for r in range(rows):
        for c in range(cols):
            if c < cols // 3 and input_grid[r][c] != 0 and len(left_col) < 3 and not left_col3:
                left_col3.append(input_grid[r][c])
            elif cols // 3 <= c < 2 * cols // 3 and input_grid[r][c] != 0 and len(middle_col) < 3 and not middle_col3:
                middle_col3.append(input_grid[r][c])
            elif 2 * cols // 3 <= c < cols and input_grid[r][c] != 0 and len(right_col) < 3 and not right_col3:
                right_col3.append(input_grid[r][c])

    if left_col2:
      left_col.append(left_col2[0])
    if left_col3:
      left_col.append(left_col3[0])
    if middle_col2:
      middle_col.append(middle_col2[0])
    if middle_col3:
      middle_col.append(middle_col3[0])
    if right_col2:
      right_col.append(right_col2[0])
    if right_col3:
      right_col.append(right_col3[0])

    while len(left_col) < 3:
        left_col.append(0)
    while len(middle_col) < 3:
        middle_col.append(0)
    while len(right_col) < 3:
        right_col.append(0)

    output_grid = [
        [left_col[0], middle_col[0], right_col[0]],
        [left_col[1], middle_col[1], right_col[1]],
        [left_col[2], middle_col[2], right_col[2]]
    ]

    return output_grid
